import{_ as l,c as i,o as a,a4 as e}from"./chunks/framework.GYzjcnJh.js";const _=JSON.parse('{"title":"Clash.Meta","description":"","frontmatter":{},"headers":[],"relativePath":"fq/clash-meta/index.md","filePath":"fq/clash-meta/index.md"}'),t={name:"fq/clash-meta/index.md"},o=e('<h1 id="clash-meta" tabindex="-1">Clash.Meta <a class="header-anchor" href="#clash-meta" aria-label="Permalink to &quot;Clash.Meta&quot;">​</a></h1><h2 id="简介¶" tabindex="-1">简介¶ <a class="header-anchor" href="#简介¶" aria-label="Permalink to &quot;简介¶&quot;">​</a></h2><p>Clash.Meta 是一个基于开源项目 Clash 的二次开发版本，增加了一些独有特性，本文档仍在修订中，欢迎 PR。</p><p>使用 Clash.Meta Kernel 前，请悉知：</p><p>本 Wiki 基于最新的 Meta Alpha 分支介绍，如果您的 Meta 低于此版本，可能会有部分功能不支持； Meta 核心支持所有原开源核心的全部特性，支持原 Clash Premium 核心部分特性。</p><h2 id="clash-meta-主要特性" tabindex="-1">Clash.Meta 主要特性 <a class="header-anchor" href="#clash-meta-主要特性" aria-label="Permalink to &quot;Clash.Meta 主要特性&quot;">​</a></h2><p>代理模块：¶</p><ol><li>支持出站传输协议 VLESS Reality, Vision 流控</li><li>支持出站传输协议 Trojan XTLS</li><li>支持出站传输协议 Hysteria</li><li>支持出站传输协议 TUIC</li><li>支持出站传输协议 ShadowTLS</li><li>支持 PASS ( 跳过 ) 规则</li><li>主动健康检测 urltest/fallback（基于 tcp 握手，限定时间内多次失败会主动触发健康检测使用节点）</li><li>支持策略组正则筛选</li><li>允许 Provider 请求通过代理更新</li><li>Proxy-Providers 支持直接使用 V2rayN 等客户端的普通订阅</li><li>Relay 代理链支持 UDP over TCP</li><li>TCP 连接并发</li></ol><h2 id="规则模块¶" tabindex="-1">规则模块¶ <a class="header-anchor" href="#规则模块¶" aria-label="Permalink to &quot;规则模块¶&quot;">​</a></h2><ol><li>支持规则 GEOSITE</li><li>支持入站类型规则 IN-TYPE</li><li>支持规则集 RULE-SET</li><li>支持规则 SRC-PORT 和 DST-PORT 的多端口条件</li><li>支持规则对 TCP / UDP 分别管控</li><li>支持 Network 规则，支持匹配网络类型 ( TCP / UDP )</li><li>支持逻辑判断规则 ( NOT / OR / AND )</li><li>支持子规则集</li><li>支持所有规则的源 IPCIDR 条件，只需附加到末尾即可</li><li>支持 GEODATA MODE 切换，mmdb / dat</li><li>支持切换 GEODATA LOADER 模式切换 , 普通 / 小内存模式</li><li>支持 GeoSite 按需加载</li><li>支持使用 geox-url 自定义的 GEOIP / GEOSITE 数据库下载地址</li></ol><h2 id="dns-模块¶" tabindex="-1">DNS 模块¶ <a class="header-anchor" href="#dns-模块¶" aria-label="Permalink to &quot;DNS 模块¶&quot;">​</a></h2><ol><li>支持 Sniffer 域名嗅探器</li><li>支持 Fallback-Filter 使用 Geosite</li><li>恢复 Redir-Host 远程解析</li><li>支持使用代理解析 ip</li><li>支持使用 Policy 分流 DNS</li><li>支持 DNS over HTTP/3</li><li>支持 DNS over QUIC</li></ol><h2 id="tun-模块¶" tabindex="-1">TUN 模块¶ <a class="header-anchor" href="#tun-模块¶" aria-label="Permalink to &quot;TUN 模块¶&quot;">​</a></h2><ol><li>支持 macOS、Linux 和 Windows</li><li>内置 iptables, 无需手动配置</li><li>内置 Wintun 驱动程序</li><li>支持 gvisor / system / lwip 堆</li></ol>',14),r=[o];function s(h,n,d,c,P,m){return a(),i("div",null,r)}const S=l(t,[["render",s]]);export{_ as __pageData,S as default};
